vec3 hsv(float h, float s, float v){
    vec3 col = vec3(
        abs(mod(h,1.0)-0.5) - 1.0/6.0,
        abs(mod(h + 1.0/3.0,1.0)-0.5) - 1.0/6.0,
        abs(mod(h + 2.0/3.0,1.0)-0.5) - 1.0/6.0
    );
    col *= v;
    float avg = (col.r + col.g + col.b)/3.0;
    col = (col - avg)*s + avg;
    return clamp(col*3.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float borderPos = iResolution.x/1.8;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).xyz;
    if (fragCoord.x < borderPos){
        col = hsv(col.x/100.0 + iTime/100.0, 1.0, col.x);
    } else {
        col = hsv(col.x/30.0 + iTime/100.0, 1.0, col.x*1.0);
    }

    float dist = distance(uv*500.0,iMouse.xy/iResolution.xy*500.0);
    col += sin(dist-iTime*10.0)/(dist*dist);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);
    float borderPos = iResolution.x/1.8;

    vec2 m = (iMouse.xy - iResolution.xy/2.0) / (iResolution.xy/2.7);
    m.x *= iResolution.x/iResolution.y;
    m.x += 0.35;
    m.y += -0.2;

    vec2 coord = (fragCoord - iResolution.xy/2.0) / (iResolution.xy/2.7);
    coord.x *= iResolution.x/iResolution.y;
    coord.x += 0.35;
    coord.y += -0.2;

    if (iChannelTime[0] < 0.5){
        if (fragCoord.x < borderPos) {
            int c = 0;
            float c_r = coord.x;
            float c_i = coord.y;
            float z_r = 0.0;
            float z_rt = 0.0;
            float z_i = 0.0;
            float size_z = 0.0;

            while (float(c) <= threshold && size_z <= 4.0) {
                z_rt = z_r;
                z_r = z_r*z_r - z_i*z_i + c_r;
                z_i = 2.0 * z_i * z_rt + c_i;
                size_z = z_r*z_r + z_i*z_i;
                c++;
            }
            if (size_z > 4.0) {
                col.r = float(c) + 1.0 - log(log(z_r*z_r + z_i*z_i)/2.0)/log(2.0);
            }
        } else if (fragCoord.x >= borderPos){
            coord.x -= 1.6;
            coord *= 1.5;

            int c = 0;
            float c_r = m.x;
            float c_i = m.y;
            float z_r = coord.x;
            float z_i = coord.y;
            float z_rt = 0.0;
            float size_z = 0.0;

            while (float(c) <= threshold && size_z <= 4.0) {
                z_rt = z_r;
                z_r = z_r*z_r - z_i*z_i + c_r;
                z_i = 2.0 * z_i * z_rt - c_i;
                size_z = z_r*z_r + z_i*z_i;
                c++;
            }
            if (size_z > 4.0){
                col = vec3(float(c)/10.0);
            }
        }
    } else {
        vec2 uv = fragCoord/iResolution.xy;
        col = texture(iChannel0, uv).xyz;
    }

    fragColor = vec4(col,1.0);
}
