vec3 getCol(vec2 uv){
    return texture(iChannel0, uv).xyz;
}

float dist(vec2 uv, vec2 uvPx, vec2 pxSize) {
    return length((uv-uvPx - pxSize/2.0)/pxSize);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvPx = floor(fragCoord/iResolution.xy*dotCount)/dotCount;
    vec2 pxSize = 1.0/dotCount;
    vec3 nearCol = getCol(uvPx);
    vec3 col = vec3(0);

    vec3 dists = vec3(dist(uv, uvPx, pxSize));

    dists *= 2.0;

    if (dists.r < getCol(uvPx).r){
        col += vec3(1,0,0);
    }
    if (dists.g < getCol(uvPx + vec2(0, pxSize.y/2.0)).g){
        col += vec3(0,1,0);
    }
    if (dists.b < getCol(uvPx + vec2(pxSize.x/2.0, 0)).b){
        col += vec3(0,0,1);
    }

    fragColor = vec4(col, 0);
}
