#define BLURAMOUNT 4.0
#define GAMMACORRECTION

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 average = vec3(0.0);

    float amount = 0.0;
    for (float y = -BLURAMOUNT; y <= BLURAMOUNT; y += 1.0) {
        for (float x = -BLURAMOUNT; x <= BLURAMOUNT; x += 1.0) {
            vec3 t = texture(iChannel0, uv + vec2(x, y) / iResolution.xy).rgb;
            #ifdef GAMMACORRECTION
            t = pow(t, vec3(2.2/1.0)); // gamma uncorrection
            #endif
            average += t;
            amount += 1.0;

        }
    } 

    vec3 col = average / amount;

    #ifdef GAMMACORRECTION
    col = pow(col, vec3(1.0/2.2)); // gamma correction
    #endif

    fragColor = vec4(col,1.0);
}
