void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = 1.0/iResolution.y;
    vec2 uv = ( fragCoord - .5* iResolution.xy )/ iResolution.y; // enlarge uv.x by x/y
    vec3 col = vec3(0.0);

    float d = length(uv) - .4;
    float c = smoothstep(w, -w, d);

    fragColor = vec4(0,0,c,1.0);
}
