renderpass.code:
float circle(vec2 center, float r, vec2 st){
    //st -= center;
    return 1.-smoothstep(r-(r*0.01),
                         r+(r*0.01),
                         distance(st, center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    float repeat = 5.;
    st = st*repeat;
    float row = ceil(st.y);
    float column = ceil(st.x);

    st = fract(st);

    vec3 col = vec3(st.x*row/repeat, st.y*column/repeat, 0.5*sin(iTime)+0.5);
    col *= vec3(circle(vec2(0.5), 0.1*sin(iTime)+0.3, st));

    fragColor = vec4(col,1.0);
}
