void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 col;

    vec4 a = texelFetch(iChannel0,ivec2(fragCoord*(iChannelResolution[0].xy/iResolution.xy)),0); //ugly math to get the images to fit in the screen
    vec4 b = texelFetch(iChannel1,ivec2(fragCoord*(iChannelResolution[1].xy/iResolution.xy)),0);
    vec4 c = texelFetch(iChannel2,ivec2(fragCoord*(iChannelResolution[2].xy/iResolution.xy)),0);

    vec4 r = (a+b+c)/3.; //mix the 3 images (using mix only works with 2 images so this is the next best thing)

    if(distance(a,r) <= distance(b,r) && distance(a,r) <= distance(c,r)){ //unoptimal but whatever
        col = a;
    }
    if(distance(b,r) <= distance(a,r) && distance(b,r) <= distance(c,r)){ 
        col = b;
    }
    if(distance(c,r) <= distance(a,r) && distance(c,r) <= distance(b,r)){ 
        col = c;
    }

    fragColor = col;
}
