float plot(vec2 st, float pct){

  return  smoothstep( pct-0.7, pct, st.y) -

          smoothstep( pct, pct+0.684, st.y);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy / iResolution.xy;
    st = (st*2.0 - -0.016) * 0.216 ;
    float y = 0.0;

    y = (((sin( st.y / 0.270 ) + st.x / -1.896) + fract( st.x / -4.066 )) + fract( st.x / st.y*2.592 )) * m.y;

    vec3 col = vec3(y);

    float pct = plot(st,y);

    col = vec3(pct);

    // Time varying pixel color
    //col = 0.5 + 0.5*cos(iTime+st.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
