const int blurRadius = 6;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 cursor = iMouse.xy;
  if (length(cursor) < 10.0) {
    cursor = vec2(
      iResolution.x * (0.5 + 0.4 * sin(iTime)), 
      iResolution.y * (0.5 + 0.4 * cos(iTime)));
  }
  if (length(fragCoord - cursor) < 15.0) {
    fragColor = vec4(1.0);
    return;
  }


  float den = 0.0;
  vec4 num = vec4(0.0);

  ivec2 iFragCoord = ivec2(fragCoord);
  for (int i = -blurRadius; i <= blurRadius; ++i) {
    vec4 col = texelFetch(iChannel0, iFragCoord + ivec2(i, 0), 0);
    float w = float(blurRadius - abs(i));

    den += float(w);
    num += col * w;
  }

  fragColor = num / den;
}

const int blurRadius = 6;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float den = 0.0;
  vec4 num = vec4(0.0);

  ivec2 iFragCoord = ivec2(fragCoord);
  for (int i = -blurRadius; i <= blurRadius; ++i) {
    vec4 col = texelFetch(iChannel0, iFragCoord + ivec2(0, i), 0);
    float w = float(blurRadius - abs(i));

    den += float(w);
    num += col * w;
  }

  fragColor = num / den;
}
