void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;

    uv *= 8.0;
    uv = fract(uv);

    uv -= 0.5;
    uv *= 2.0;

    float x = uv.x;
    float y = uv.y;

    float time = -abs(2.0 * ((mod(iTime * 0.5, 2.0) - 1.0))) + 1.0;

    float dotP = dot(normalize(uv), vec2(1.0, 0.0));

    float len = length(uv);
    if (dotP > time && y > 0.0 && len < 1.0) {
        fragColor = vec4(col, 1.0) * (1.0 - smoothstep(0.95, 1.0, len));
        return;
    }
    if (y < 0.0 && len < 1.0 && dotP < time) {
        fragColor = vec4(col, 1.0) * (1.0 - smoothstep(0.95, 1.0, len));
        return;
    }
    fragColor = vec4(0.0);
}
