#define miter 40
#define citer 50
#define delta 1e-8

float lm(float a, float r)
{
    return r*a*(1.0-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float r = (1.- 1. / (iTime + 1.5))*4.;
    float r_scaled = r*(1.-1./(iTime+delta)) + (uv.x+.5)*r*(1./(iTime+delta));

    float f = 3e38;
    for (int i=0; i<citer; ++i)
    {
        float a = float(i)/float(citer);
        for (int j=0; j<miter; ++j)
            a = lm(a, r_scaled);
        f = min(f, abs(a-uv.y));
    }

    vec3 line = vec3(1.-smoothstep(uv.x, 0.4999, 0.501));
	fragColor = vec4(line + vec3(1.0-smoothstep(f, 0.0, 0.0005)), 1.0 );
}

#define TWOPI 6.2831

vec2 mainSound( int samp, float time )
{
    float r = (1.- 1. / (time + 1.5))*4.;

    float x = .5;
    int i;
    for(i=0;i<100;i++) {
        x = r * x * (1. - x);
    }

    float f, freq;
    for(i=0;i<200;i++) {
        freq = 261.63 + 261.63 * x;
        f += sin(TWOPI*freq*time);
        x = r * x * (1. - x);
    } f /= 200.;

    return vec2( f );
}
