const float NOISE_SCALE = 0.80;
const float NOISE_SPEED = .5;
const float WAVE_SHIFT_IMPACT = .2;
const float WAVE_SPEED = 5.;
const float WAVE_FREQUENCY = 80.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 st = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 noiseSt = st * NOISE_SCALE;
    vec2 noise = vec2(Perlin3D(vec3(noiseSt, iTime * NOISE_SPEED)) + 1.) / 2.;
    vec2 uvShift = vec2(
        sin(iTime * WAVE_SPEED + noise * WAVE_FREQUENCY)
    ) * WAVE_SHIFT_IMPACT;
    vec3 color = texture(
        iChannel1,
        uv - uvShift
    ).rgb;
    fragColor = vec4(color, 1.0);
}

