// Author: Lydia MacBride
// Title: Trásinscneach

#define PI 3.14159265359

// Wave values
const float a = float(0.05);
const float f = float(PI *2.0);

// Define Colours
const vec3 blue = vec3(0.357, 0.812, 0.98);
const vec3 pink = vec3(0.961, 0.671, 0.725);
const vec3 white = vec3(1.0, 1.0, 1.0);

float wave(in vec2 i) {
    float z = i.y + (a * sin(iTime + (i.x * f)));
    return z;
}

float udRoundBox(in vec2 p, in vec2 b, in float r) {
  return length(max(abs(p) - b, 0.0)) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = gl_FragCoord.xy / iResolution.xy;
    st.x *= iResolution.x / iResolution.y;

    float x = (step(0.2, wave(st)) - step(0.8, wave(st)));
    vec3 bp = mix(blue, pink, x);

    x = step(0.4, wave(st)) - step(0.6, wave(st));
    vec3 color = mix(bp, white, x);

    fragColor = vec4(color, 1.0) * udRoundBox(fragCoord, vec2(1.0, 1,0), 0.1);
}
