float halftone(vec2 uvw, float n) {
    vec2 uv = uvw.xy * 2.0;
    vec2 subuv = fract(uv);

    vec2 fo = mod(uv - subuv,vec2(2.0))-0.5;
    return length(subuv + fo*n - 0.5) / sqrt(1.1 + n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 n = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float s = iResolution.y / 50.0;
    vec2 suv = uv * s;

    float w = n.y;
    float h = n.x;

    w = clamp(w, 0.0, 1.0);
    h = clamp(h, 0.0, 1.0);

    float c = halftone(suv, w * w);

    c = clamp((w - c)*10.0, 0.0, 1.0);

    fragColor = vec4(vec3(c),1.0);
}
