void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 c1 = texture(iChannel0, uv);
    vec4 c2 = texture(iChannel1, uv);

    vec4 alpha = vec4(0.9,0.2,0.8,0.5);

    fragColor = mix ( c1, c2, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;

    vec4 col = vec4(20.0/(10.0) * m.x, 20.0/(10.0) * m.y, 0.0, 0.0);

    // Output to screen
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    if(iMouse.z > 0. && length(iMouse.xy - fragCoord) < 10.) {
        fragColor = col;
    }
}

vec4 welovecircle(in vec2 uv, in vec2 m, in float x, in float y, in float xAxe, in float positive) {  

    vec4 col = vec4(0.0);

    if(distance(uv, vec2(x * positive * sin(iTime)/2.0 + xAxe,y)) < 0.08) {
        col = vec4(1.0/(x*50.0) * m.x , 1.0/(x*50.0) * m.y , 0.0 ,0.0);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{       
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;

    if(iMouse.xy == vec2(0,0)) { // Thanks FabriceNeyret2
        m = vec2(0.5,0.5);
    }

    vec4 col;

    for(float i = 10.0; i < 100.0; i++) {

        col += welovecircle(uv, m, i/100.0, i/100.0, 0.1, -1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.2, 1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.3, -1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.4, 1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.5, -1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.6, 1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.7, -1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.8, 1.0);
        col += welovecircle(uv, m, i/100.0, i/100.0, 0.9, -1.0);

        fragColor = texture(iChannel1, uv); 
        fragColor = col;
    }
}
