//Daily Shader Day 8
//Tried making a shader in 15 ish minutes, while still getting the theme of the number in time
//Lopea (https://github.com/lopea)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv).r * vec4(.5,.4,.9,1);
}

//Daily Shader Day 8
//Tried making a shader in 15 ish minutes, while still getting the theme of the number in time
//Lopea (https://github.com/lopea)

void mainImage( out vec4 c, in vec2 f )
{
    vec2 r = iResolution.xy, u = (2. * f - r)/r.y;
    float s = length(u - vec2(-sin(iTime)*cos(iTime), cos(iTime) *.65)) - .05;
    c.r = step(s, 0.001) + texture(iChannel0, f/r).r *0.992;
}
