void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5);

    float c = clamp(sin(uv.x*uv.x*uv.y*uv.y*3000.0 + 3.0 *iTime), 0.0, 1.0);
    float d = clamp(sin((uv.x*uv.y*uv.x)*1000.0 - 3.0 * iTime), 0.0, 1.0);

    float r = max(d, c);

    // Output to screen
    fragColor = vec4(vec3(r),1.0);
}
