vec2 rotate(vec2 v, float angle) {
    return vec2(
            v.x * cos(angle) - v.y * sin(angle),
            v.x * sin(angle) + v.y * cos(angle)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 startColor = vec4(1.0, 0.2, 0.2, 1.0);
    vec4 endColor = vec4(0.2, 0.2, 1.0, 1.0);

    vec2 uv = fragCoord/iResolution.xy;

    vec2 point = vec2(0.5, 0.5);

    vec2 relative = rotate(uv - point, iTime);

    float angle = atan(relative.x, relative.y);
    float t = (angle + 3.14) / 2.0 / 3.14;

    fragColor = mix(startColor, endColor, t);
}
