const float PI = 3.14159;

float startX = 210.0;
float startY = 118.0;
float tilt = PI/2.0;
float sf = 0.05;
float phase = 0.0;
float sc = 20.0;
float contrast = 0.5;
float drift = 0.5;

float time = 0.0;

void mainImage( out vec4 fragColor, in vec2 xy )
{
    vec2 uv = xy - vec2(iResolution.x/2.0, iResolution.y/2.0);

    float sth = sin(tilt);
    float cth = cos(tilt);

    float env = exp(- 0.5 / (sc*sc) * (uv.x * uv.x + uv.y * uv.y));

    float color = 0.5+env*contrast*sin(2.0*PI*(uv.x*sf*cth + uv.y*sf*sth)- iTime * 60.0 * drift +phase);

    fragColor = vec4( color, color, color, 1.0 );
}
