/*

 playing with colors again

*/


#define PI 3.14159
#define TWO_PI (PI * 2.0)
#define N 12.0


vec3 hsb2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6. + vec3(0. , 4., 2.), 6.) - 3.) - 1., 0., 1. );
    rgb = rgb * rgb * (3. - 2. * rgb);
    return c.z * mix(vec3(1.), rgb, c.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;


    float steppi = sin(uv.x*88.  )*.5+.5;

	steppi += cos(uv.y*49.  )*.5+.5;

    steppi *= sin(uv.y*5. + iTime)*.5+.5;

	steppi += cos(steppi*4.);



     float f = steppi; 


	float shift = iMouse.x / iResolution.x;

	float spread = iMouse.y*3. / iResolution.y;

	f += shift*4.;



	vec3 col = vec3(
	cos(f)*.5+.5,
	cos(f*spread )*.5+.5,
	cos(f*spread*3.)*.5+.5
	);

	col = hsb2rgb(col);


   	 fragColor = vec4(vec3(col), 1.0);

}
