// Happy Holidays, December 2021
// by Tito Morab - ChutneyPot
// https://www.shadertoy.com/view/NlGXDh
// Music based on Jingle Bell Rock by Bobby Helms
//--------------------------------------------------------------------------
#define SKY_COLOR vec4(0.38, 0.49, 0.64, 1.0)
#define BEAM_COLOR vec4(0.47, 0.56, 0.69, 1.0)
#define SNOW_COLOR vec4(0.92, 0.93, 0.95, 1.0)
#define WINDOW_COLOR vec4(0.4, 0.27, 0.23, 1.0)
#define WALL_COLOR1 vec4(0.55, 0.43, 0.43, 1.0)
#define WALL_COLOR2 vec4(0.51, 0.39, 0.38, 1.0)
#define FLOOR_COLOR vec4(0.33, 0.25, 0.22, 1.0)
#define FIREPLACE_COLOR1 vec4(0.82, 0.83, 0.85, 1.0)
#define FIREPLACE_COLOR2 vec4(0.54, 0.55, 0.57, 1.0)
#define FIREPLACE_COLOR3 vec4(0.29, 0.22, 0.32, 1.0)
#define DECO_COLOR1 vec4(0.90, 0.65, 0.26, 1.0)
#define DECO_COLOR2 vec4(0.77, 0.31, 0.20, 1.0)
#define DECO_COLOR3 vec4(0.20, 0.31, 0.77, 1.0)
#define DECO_COLOR4 vec4(0.27, 0.55, 0.38, 1.0)
#define DECO_COLOR5 vec4(0.27, 0.75, 0.68, 1.0)
#define WIRE_COLOR vec4(0.32, 0.30, 0.31, 1.0)
#define LIGHT_COLOR1 vec4(0.91, 0.91, 0.49, 1.0)
#define LIGHT_COLOR2 vec4(0.80, 0.81, 0.87, 0.5)

#define BLUR 4.0 / iResolution.y

//https://iquilezles.org/articles/distfunctions2d
float Cir(vec2 p, float r)
{
    return length(p) - r;
}

float Box(in vec2 p, in vec2 b)
{
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

float Tri(in vec2 p)
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0 / k;

    if(p.x + k * p.y > 0.0)
        p = vec2(p.x - k * p.y, -k * p.x - p.y) / 2.0;
    p.x -= clamp(p.x, -2.0, 0.0);
    return -length(p) * sign(p.y);
}

vec2 Rot(in vec2 uv, in float ang)
{
    ang = radians(ang);
    vec2 res = mat2(cos(ang), sin(ang), -sin(ang), cos(ang)) * uv;
    return res;
}

float Snow(vec2 uv, float time, float speed, float radius)
{
    float ar = iResolution.x / iResolution.y;
    uv.x += ar - mod(time * speed, ar * 0.7);
    uv.y -= 1.0 - mod(time * speed, 1.5);

    uv += sin(time) * 0.1;

    float l = length(uv);    
    return smoothstep(radius, radius - BLUR, l);   
}

vec4 Env(in vec2 uv)
{
    float beam1 = smoothstep(0.3, 0.3 - BLUR, abs(uv.x + uv.y + 1.4));
    float beam2 = smoothstep(0.1, 0.1 - BLUR, abs(uv.x + uv.y + 0.5));

    float sc1 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv - vec2(-1.1, -0.45), -5.0), vec2(0.35, 0.05)) - 0.06);
    float sc2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv - vec2(-0.7, 0.1), 3.0), vec2(0.02, 0.33)) - 0.06);
    float sc3 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(-0.55, -1.7), 1.5));
    float sc = max(max(sc1, sc2), sc3);

    float s = Snow(uv, iTime + 0.0, 0.4, 0.01);
    s += Snow(uv, iTime + 1.0, 0.3, 0.025);
    s += Snow(uv, iTime + 2.0, 0.2, 0.01);
    s += Snow(uv, iTime + 3.0, 0.2, 0.015);
    s += Snow(uv, iTime + 4.0, 0.4, 0.02);
    s += Snow(uv, iTime + 5.0, 0.2, 0.02);
    s += Snow(uv, iTime + 6.0, 0.3, 0.01);
    s += Snow(uv, iTime + 7.0, 0.2, 0.01);
    s += Snow(uv, iTime + 8.0, 0.2, 0.025);
    s += Snow(uv, iTime + 9.0, 0.4, 0.02);  

    vec4 col = SKY_COLOR; 
    col = mix(col, BEAM_COLOR, beam1 + beam2);
    col = mix(col, SNOW_COLOR, max(s, sc));
    return col;
}

vec4 Win(in vec2 uv)
{    
    vec4 env = Env(uv);

    uv -= vec2(-1.14, 0.1);
    uv = abs(uv);

    float oe = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.21, 0.25), vec2(0.21, 0.25)));
    float ie = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.2, 0.24), vec2(0.18, 0.22)));

    vec4 col = vec4(0.0); 
    col = mix(vec4(vec3(env.rgb), 0.0), WINDOW_COLOR, vec4(vec3(oe - ie), oe));
    return col;
}

vec4 Wall(in vec2 uv)
{
    uv *= 20.0;
    float wall = smoothstep(0.35, 0.35 - BLUR, fract(uv.x - 0.32));
    uv /= 20.0;

    float base = smoothstep(0.02, 0.02 - BLUR, Box(uv - vec2(0.0, -0.75), vec2(2.8, 0.04)));
    vec4 win = Win(uv);

    vec4 col = vec4(0.0);
    col = mix(WALL_COLOR1, WALL_COLOR2, wall);
    col = mix(col, WALL_COLOR2, base);
    col = mix(col, win, win.w);

    return col;
}

vec4 Gnd(in vec2 uv)
{
    float fl = smoothstep(0.02, 0.02 - BLUR, Box(uv - vec2(0.0, -1.0), vec2(2.8, 0.2)));

    float g1 = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(-1.415, -0.7), vec2(0.12, 0.15)) - 0.01);
    float go1 = smoothstep(0.01, 0.01 - BLUR, Box((uv - vec2(-1.415, -0.65)), vec2(0.13, 0.02)));
    float gi1 = smoothstep(0.01, 0.01 - BLUR, Box(abs(uv - vec2(-1.45, -0.7)) - vec2(0.02, 0.0), vec2(0.005, 0.16)));

    float g2 = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(-1.06, -0.82), vec2(0.2, 0.03)) - 0.01);
    float go2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(abs(uv - vec2(-1.06, -0.82)) - vec2(0.15, 0.0), 75.0), vec2(0.001, 0.04)));
    float gi2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(abs(uv - vec2(-1.06, -0.82)) - vec2(0.05, 0.0), 75.0), vec2(0.001, 0.043)));

    float g3 = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(-1.03, -0.705), vec2(0.1, 0.05)) - 0.01);    
    float go3 = smoothstep(0.01, 0.01 - BLUR, Box(abs(uv - vec2(-1.03, -0.705)) - vec2(0.0, 0.05), vec2(0.11, 0.001)));
    float gi3 = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(-1.03, -0.705), vec2(0.11, 0.001)));

    float g4 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv - vec2(-0.76, -0.7), 40.0), vec2(0.2, 0.04)) - 0.01);
    float go4 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv - vec2(-0.7, -0.75), 40.0), vec2(0.01, 0.05)));
    float gi4 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv - vec2(-0.76, -0.7), 40.0), vec2(0.21, 0.0)));

    vec4 col = vec4(0.0);
    col = mix(col, FLOOR_COLOR, fl);
    col = mix(col, DECO_COLOR1, g1);
    col = mix(col, DECO_COLOR4, max(go1, gi1));
    col = mix(col, DECO_COLOR5, g2);
    col = mix(col, DECO_COLOR3, max(go2, gi2));
    col = mix(col, DECO_COLOR2, g3);
    col = mix(col, DECO_COLOR3, max(go3, gi3));
    col = mix(col, DECO_COLOR3, g4);
    col = mix(col, DECO_COLOR2, max(go4, gi4));
    return col;
}

vec4 Lrl(in vec2 uv)
{
    uv -= vec2(0.0, 0.95);
    uv *= 20.0;
    float c = smoothstep(0.9, 0.9 - BLUR, abs(Cir(uv, 0.01) - 3.0));

    uv.x = abs(uv.x);
    float b = smoothstep(0.2, 0.2 - BLUR, Tri(Rot(Rot(uv, 15.0) - vec2(0, 3), 15.0)));

    uv = abs(uv);   
    float l1 = smoothstep(0.3, 0.3 - BLUR, Tri(Rot(Rot(uv, 15.0) - vec2(0, 3), 25.0)));
    float l2 = smoothstep(0.3, 0.3 - BLUR, Tri(Rot(Rot(uv, 45.0) - vec2(0, 3), 30.0)));
    float l3 = smoothstep(0.3, 0.3 - BLUR, Tri(Rot(Rot(uv, 75.0) - vec2(0, 3), 30.0)));
    float l = max(max(max(l1, l2), l3), c);

    float g = smoothstep(0.02, 0.02 - BLUR, abs(Cir(uv - vec2(-0.1, 3.5), 3.5)) - 0.2);

    vec4 col = vec4(0.0);
    col = mix(col, DECO_COLOR4, l);
    col = mix(col, DECO_COLOR2, max(b, min(g, l)));
    return col;
}

vec4 Scks(in vec2 uv)
{
    uv -= vec2(-0.3, 0.25);
    float t1 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0) - vec2(0.0, 0.07), vec2(0.05, 0.004)) - 0.01);
    float bs1 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0), vec2(0.025, 0.05)) - 0.01);
    float bt1 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(Rot(uv, 20.0) - vec2(0.026, -0.06), 15.0), vec2(0.04, 0.01)) - 0.02);
    float h1 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(-0.05, -0.05), 0.02));
    float b1 = max(bs1, bt1);
    float w1 = max(t1, min(h1, b1));

    uv.x -= 0.3;
    float t2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0) - vec2(0.0, 0.07), vec2(0.05, 0.004)) - 0.01);
    float bs2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0), vec2(0.025, 0.05)) - 0.01);
    float bt2 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(Rot(uv, 20.0) - vec2(0.026, -0.06), 15.0), vec2(0.04, 0.01)) - 0.02);
    float h2 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(-0.05, -0.05), 0.02));
    float b2 = max(bs2, bt2);
    float w2 = max(t2, min(h2, b2));

    uv.x -= 0.3;
    float t3 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0) - vec2(0.0, 0.07), vec2(0.05, 0.004)) - 0.01);
    float bs3 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(uv, 20.0), vec2(0.025, 0.05)) - 0.01);
    float bt3 = smoothstep(0.01, 0.01 - BLUR, Box(Rot(Rot(uv, 20.0) - vec2(0.026, -0.06), 15.0), vec2(0.04, 0.01)) - 0.02);
    float h3 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(-0.05, -0.05), 0.02));
    float b3 = max(bs3, bt3);
    float w3 = max(t3, min(h3, b3));

    vec4 col = vec4(0.0);
    col = mix(col, DECO_COLOR1, b1);
    col = mix(col, DECO_COLOR2, max(bs2, bt2));
    col = mix(col, DECO_COLOR3, max(bs3, bt3));
    col = mix(col, SNOW_COLOR, max(max(w1, w2), w3));
    return col;
}


vec4 Fire(in vec2 uv)
{
    uv *= vec2(1.2, 1.0);
    float foo1 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(0.0, -0.15), mix(0.148, 0.15, sin(iTime * 10.0) * 0.5 + 0.5)));
    float foi1 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(0.04, -0.08), 0.1));  
    float f1 = clamp(foo1 - foi1, 0.0, 1.0);

    float foo2 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(0.0, -0.17), mix(0.06, 0.08, sin(iTime * 0.5) * 0.5 + 0.5)));
    float foi2 = smoothstep(0.01, 0.01 - BLUR, Cir(uv - vec2(0.07, -0.13), mix(0.05, 0.07, sin(iTime * 0.5) * 0.5 + 0.5)));
    float f2 = clamp(foo2 - foi2, 0.0, 1.0);

    vec4 col = vec4(0.0);
    col = mix(col, mix(DECO_COLOR1, DECO_COLOR2, (uv.x + uv.y) / 0.3 + 1.2), max(f1, f2));
    return col;
}

vec4 Fplc(in vec2 uv)
{       
    uv -= vec2(0.0, -0.5);
    float b = smoothstep(0.01, 0.01 - BLUR, Box(uv, vec2(0.45, 0.35)));
    float bi = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.0, -0.1), vec2(0.28, 0.2)));

    vec2 auv = vec2(abs(uv.x), uv.y);    
    float bsi = smoothstep(0.01, 0.01 - BLUR, Box(auv - vec2(0.35, -0.1), vec2(0.01, 0.2)));
    float bso = smoothstep(0.01, 0.01 - BLUR, Box(auv - vec2(0.4, -0.1), vec2(0.01, 0.2)));

    float t = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.0, 0.39), vec2(0.5, 0.04)));
    float tih = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.0, 0.39), vec2(0.47, 0.01)));
    float til = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.0, 0.34), vec2(0.4, 0.01)));

    float c = smoothstep(0.01, 0.01 - BLUR, Box(uv - vec2(0.0, 0.9), vec2(0.25, 0.6)));

    vec4 fire = Fire(uv);
    vec4 lrl = Lrl(uv);
    vec4 scks = Scks(uv);

    vec4 col = vec4(0.0);
    col = mix(col, FIREPLACE_COLOR1, c);
    col = mix(col, FIREPLACE_COLOR1, max(b, t));
    col = mix(col, FIREPLACE_COLOR3, bi);
    col = mix(col, FIREPLACE_COLOR2, tih + til + bsi + bso);
    col = mix(col, fire, fire.w);
    col = mix(col, lrl, lrl.w);
    col = mix(col, scks, scks.w);
    return col;
}

vec4 Tree(in vec2 uv)
{
    uv -= vec2(1.14, -0.15);
    float t1 = smoothstep(0.02, 0.02 - BLUR, Tri(uv * 5.5 - vec2(0.0, 2.5)) - 0.2);
    float t2 = smoothstep(0.02, 0.02 - BLUR, Tri(uv * 4.5 - vec2(0.0, 1.0)) - 0.2);
    float t3 = smoothstep(0.02, 0.02 - BLUR, Tri(uv * 3.5 - vec2(0.0, -0.2)) - 0.2);
    float t4 = smoothstep(0.02, 0.02 - BLUR, Tri(uv * 3.0 - vec2(0.0, -1.0)) - 0.2);
    float t = max(max(max(t1, t2), t3), t4);

    float tr = smoothstep(0.02, 0.02 - BLUR, Box(uv - vec2(0.0, -0.58), vec2(0.06, 0.1)));

    float s1 = smoothstep(0.02, 0.02 - BLUR, Tri(uv * 20.0 - vec2(0.0, 14.0)) - 0.1);
    float s2 = smoothstep(0.02, 0.02 - BLUR, Tri(Rot(uv * 20.0 - vec2(0.0, 14.0), 60.0)) - 0.1);

    float g1 = smoothstep(0.02, 0.02 - BLUR, abs(Cir(uv - vec2(-0.1, 0.7), 0.3)) - 0.001);
    float g2 = smoothstep(0.02, 0.02 - BLUR, abs(Cir(uv - vec2(-0.1, 0.5), 0.4)) - 0.001);
    float g3 = smoothstep(0.02, 0.02 - BLUR, abs(Cir(uv - vec2(-0.1, 0.3), 0.5)) - 0.001);
    float g4 = smoothstep(0.02, 0.02 - BLUR, abs(Cir(uv - vec2(-0.15, 0.18), 0.7)) - 0.001);

    uv -= vec2(-0.05, -0.05);
    float b1 = smoothstep(0.02, 0.02 - BLUR, Cir(abs(Rot(uv, 8.0)) - vec2(0.17, 0.45), 0.005));

    uv -= vec2(0.08, -0.02);
    float b2 = smoothstep(0.02, 0.02 - BLUR, Cir(abs(Rot(uv, -15.0)) - vec2(0.21, 0.23), 0.02));

    uv = Rot(uv, -15.0);
    uv -= vec2(0.0, 0.1);
    uv.y = abs(uv.y);
    float b3 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.0, 0.2), 0.01));

    vec4 col = vec4(0.0);
    col = mix(col, WINDOW_COLOR, tr);
    col = mix(col, DECO_COLOR4, t);
    col = mix(col, DECO_COLOR1, max(s1, s2));
    col = mix(col, DECO_COLOR2, min(max(g1, g4), t));
    col = mix(col, DECO_COLOR1, min(g2, t));
    col = mix(col, DECO_COLOR3, min(g3, t));
    col = mix(col, DECO_COLOR3, b1);
    col = mix(col, DECO_COLOR2, b2);
    col = mix(col, DECO_COLOR1, b3);
    return col;
}

vec4 Lght(in vec2 uv)
{
    uv.x = abs(uv.x);
    float wo = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.9, 2.2), 1.4));
    float wi = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.9, 2.2), 1.395));

    uv.x *= 1.4;
    float l1 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.3, 0.935), 0.008));
    float l2 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.6, 0.84), 0.008));
    float l3 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(0.9, 0.785), 0.008));
    float l4 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(1.2, 0.76), 0.008));
    float l5 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(1.5, 0.77), 0.008));
    float l6 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(1.8, 0.81), 0.008));
    float l7 = smoothstep(0.02, 0.02 - BLUR, Cir(uv - vec2(2.1, 0.89), 0.008));
    float l = l1 + l2 + l3 + l4 + l5 + l6 + l7;

    vec4 col = vec4(0.0);
    col = mix(col, WIRE_COLOR, wo - wi);
    col = mix(col, mix(LIGHT_COLOR1, LIGHT_COLOR2, step(0.5, sin(iTime * 2.0 + 0.5) * 0.5 + 0.5)), l);
    return col;
}

vec4 Scene(in vec2 uv)
{
    vec4 col = vec4(0.0);  
    vec4 wall = Wall(uv);
    vec4 gnd = Gnd(uv);
    vec4 fplc = Fplc(uv);
    vec4 tree = Tree(uv);
    vec4 lght = Lght(uv);

    col = mix(col, wall, wall.w);
    col = mix(col, gnd, gnd.w);
    col = mix(col, fplc, fplc.w);
    col = mix(col, tree, tree.w);
    col = mix(col, lght, lght.w); 
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float c = smoothstep(0.09, 0.09 - BLUR, abs(length(uv) - 0.3));

    fragColor = Scene(uv);
}
