float dHeart(vec2 uv){
    uv.x*=.7;
    uv.y-=sqrt(abs(uv.x))*.5;
    uv.y+=.1;
    float d=length(uv);
    return d;
}
float dis(vec2 p,vec2 q){
    //return max(abs(p.x-q.x),abs(p.y-q.y));//rect
    //return length(p-q);//circle
    return dHeart(p-q);
}
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
//// See https://iquilezles.org/articles/palettes for more information
    return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scale = 10.*vec2(iResolution.x/iResolution.y,1.0);
    vec2 newUV=uv*scale;
    vec2 id=floor(newUV);
    vec2 offset=fract(newUV);
    vec2 maskUV=id/scale;
    // Time varying pixel color
    vec3 col=vec3(0.0);
    vec2 lastUV;
    float tempScale=0.;
    for(int i=-1;i<=1;i++)
        for(int j=-1;j<=1;j++){
            vec2 idOffset = vec2(i,j);
            vec2 newid= id+idOffset;
            float newid_rand = N21(newid);
            float maskScale=newid_rand*1.2+0.7;
            if(dis(offset,idOffset+0.5)*2.<maskScale&&tempScale<maskScale){
                lastUV=newid/scale;

                tempScale=maskScale;
            }
        }
    // Output to screen
    float index = N21(lastUV);
    col=pal( index, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.10,0.20) );
    fragColor = vec4(col,1.0);
}
