void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float cB = 0.0; 
    float cG = 0.0;
    float cR = 0.0;
    float step1 =0.0;
    float step2 = 0.0;
    float modR = 0.0;
    float modG = 0.0;
    float modB = 0.0;
    float barsize=2.2+(sin(iTime*2.5+3.32*uv.y))*2.2+0.8;
    float halfsize = barsize/2.;

   float maxbars=141.-(140.*uv.y);

    for (float licz=1. ; licz<=maxbars ; ++licz){
    float samplepoint = sin(iTime*1.1+step1)*2.3+sin(iTime*2.3+step2)*2.3-4.6+uv.x*10.0+halfsize;

    if ((samplepoint > 0.) && (samplepoint < barsize)) {

    cR=sin((samplepoint)*3.2/barsize)*modR;
    cG=sin((samplepoint)*3.2/barsize)*modG;
    cB=sin((samplepoint)*3.2/barsize)*modB;
    }

    modR = modR+0.02;
    modG = modG+0.01;
    modB = modB+0.08;

    step1 = step1+0.093;
    step2 = step2-0.063;
    }

    // Output to screen
    fragColor = vec4(cR,cG,cB,1.0);
}
