/**
 * Just a chromatic aberration post processing test.
 * Sorry if you suffer from motion sickness.
 */

// Maximum aberration per channel in pixels.
float r_strength = 5.0f;
float g_strength = 5.0f;
float b_strength = 5.0f;

// Oscillation speed for red channel.
float r_freq_x = 8.0f;
float r_freq_y = 4.8f;

// ... for green channel
float g_freq_x = 7.3f;
float g_freq_y = 6.6f;

// ... for blue channel
float b_freq_x = 9.4f;
float b_freq_y = 10.1f; 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 r_shift = vec2(sin(t * r_freq_x), sin(t * r_freq_y)) * r_strength / iResolution.xy;
    vec2 g_shift = vec2(sin(t * g_freq_x), sin(t * g_freq_y)) * g_strength / iResolution.xy;
    vec2 b_shift = vec2(sin(t * b_freq_x), sin(t * b_freq_y)) * b_strength / iResolution.xy;

    float r = texture(iChannel0, uv + r_shift).r;
    float g = texture(iChannel0, uv + g_shift).g;
    float b = texture(iChannel0, uv + b_shift).b;

    fragColor = vec4(r, g, b, 1.0f);
}
