vec3 applyKernel(mat3 G, vec4[9] A){
    vec3 res = vec3(0.);

    for(int i=0; i<3 ;i++)
        for(int j=0; j<3 ;j++)
            res += A[i*3+j].xyz*G[i][j];

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 s  = 1./iResolution.xy;

    vec4[9] A = vec4[9]( texture(iChannel0,uv+vec2(-s.x,-s.y)) ,texture(iChannel0,uv+vec2(-s.x,0.)) ,texture(iChannel0,uv+vec2(-s.x,s.y)) ,
                         texture(iChannel0,uv+vec2(  0.,-s.y)) ,texture(iChannel0,uv)               ,texture(iChannel0,uv+vec2(  0.,s.y)) ,
                         texture(iChannel0,uv+vec2( s.x,-s.y)) ,texture(iChannel0,uv+vec2( s.x,0.)) ,texture(iChannel0,uv+vec2( s.x,s.y)));

    vec3 Gx_A = applyKernel(mat3(-1., 0., 1.,
                                 -2., 0., 2.,
                                 -1., 0., 1.) , A);

    vec3 Gy_A = applyKernel(mat3(-1.,-2.,-1.,
                                  0., 0., 0.,
                                  1., 2., 1.) , A);

    fragColor = vec4(sqrt(Gx_A*Gx_A + Gy_A*Gy_A),0.);
}
