float s(in float x_, in float y_)

{
    //make grid based on mod1, ranging from [-0.5,0.5]
    float x = x_ - float(int(x_)) - 0.5;
    float y = y_ - float(int(y_)) - 0.5;

    //if r < triangle(theta) return 1 else return 0
    float r = sqrt(x*x + y*y);
    float p = 6.28 / 5.0;
    float theta = acos(-y/r);
    float rmin = 0.25 * abs(theta / p - floor(theta / p + 0.5)) + 0.125;
    return max(1.0 - float(int(r / rmin)),0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //uv offset for wave effect
    uv.y += 0.02 * cos(uv.x * 20.0 + 2.0 * iTime);
    uv.x += 0.01 * cos(cos(uv.y * 3.0 + 2.0 * iTime) + uv.x * 20.0 + 2.0 * iTime);
    //shading for wave effect
    float wave_shading = 0.7 + 0.2 * cos(uv.x * 20.0 + 2.0 * iTime);

    //stripes
    float stripe = float(int(floor(uv.y * 13.0)) % 2);
    vec3 stripes = vec3(1.0, stripe, stripe);

    //stars
    float star_x = uv.x * 5.0/2.0;
    float star_y = uv.y * 13.0/7.0;
    float star1 = s(star_x * 6.0 , star_y * 5.0 - 0.25);
    float star2 = s(star_x * 6.0 + 0.5, star_y * 5.0 + 0.25);
    float star = star1 + star2;

    //stencils
    float sx = uv.x - 0.02 - floor(uv.x - 0.02);
    float sy = uv.y + 0.026 - floor(uv.y + 0.026);
    float s_stencil = max(0.0, 1.0 - float(max(int(sx * 2.75), int((1.0 - sy) * 2.04))));
    vec3 stars = vec3(s_stencil * star, s_stencil * star, 1.0);

    float stencil_1 = 1.0 - min(1.0, floor(uv.x * 5.0 / 2.0));
    float stencil_2 = 1.0 - min(1.0, floor((1.0 - uv.y) * 13.0 / 7.0));
    float stencil = stencil_1 * stencil_2;


    vec3 flag = wave_shading * (stencil * stars + (1.0 - stencil) * stripes);

    // Output to screen
    fragColor = vec4(flag, 1.0);
}
