// Signed distance to a disk

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d


float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

float sdStar(in vec2 p, in float r, in int n, in float m) // m=[2,n]
{
    // these 4 lines can be precomputed for a given shape
    float an = 3.141593/float(n);
    float en = 3.141593/m;
    vec2  acs = vec2(cos(an),sin(an));
    vec2  ecs = vec2(cos(en),sin(en)); // ecs=vec2(0,1) and simplify, for regular polygon,

    // reduce to first sector
    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));

    // line sdf
    p -= r*acs;
    p += ecs*clamp( -dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

// b.x = width
// b.y = height
// r.x = roundness top-right  
// r.y = roundness boottom-right
// r.z = roundness top-left
// r.w = roundness bottom-left
float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

vec4 borderRadius = vec4(0.5, 0.3, 0.7, 0.5);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    vec2 p1 = vec2(p.x + sin(iTime * 0.5), p.y - sin(1.5 - iTime * 0.5));
    vec2 p2 = vec2(p.x + 4.0-mod(iTime * 0.2, 4.0) * 2.0, p.y - sin(1.5 - iTime * 0.02));
	float d1 = sdStar(p1, 1.5 + sin(iTime) * 0.1, 5, 3.5 + sin(iTime));
    float d2 = sdStar(p2, 1.3, 3, 2.0 + sin(iTime) * 0.5);
    float d3 = sdRoundedBox(p + -cos(iTime*0.3), vec2(0.8 + sin(iTime * 1.6) * 0.1, 0.8 + sin(iTime * 0.3) * 0.1), borderRadius);

	// coloring
    vec3 red = vec3(0.9, 0.46, 0.45);
    vec3 green = vec3(0.52, 0.8, 0.53);
    vec3 purp = vec3(0.49, 0.34, 0.61);
    vec3 orange = vec3(0.82, 0.5, 0.36);
    vec3 col = red;
    col = mix(col, green, clamp(1.0-d1, 0.0, 1.0));
    col = mix(col, purp, clamp(1.0-d2, 0.0, 1.0));
    col = mix(col, orange, clamp(1.0-d3, 0.0, 0.5));


	fragColor = vec4(col,1.0);
    //fragColor = vec4(purp * d3, 1.0);
}
