void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
    if (uv.x < sin(iTime)-0.005)
        col = vec3(smoothstep(0.49, 0.5, length(uv)));
    else if (uv.x > sin(iTime)+0.005)
        col = vec3(length(uv) >= 0.5);
    else
        col = vec3(1., 0., 0.);

    fragColor = vec4(col,1.0);
}
