#define PI 3.1415926535897


mat2 rotate(float rad){
    vec2 v60 = vec2( cos(rad), sin(rad));
    mat2 rot60 = mat2(v60.x,-v60.y,v60.y,v60.x);
    return rot60;
}

vec3 line(vec2 uv ,   float lineWidth){

   float top = step( 0.0  , uv.y);
   float bottom = step( iResolution.y   - lineWidth   , iResolution.y -uv.y);
   vec3 empty_color = vec3(0.0);
   vec3 white = vec3(1.0);
   vec3 yellow = vec3(1.0,0.8,0.4);

   vec3 blue = vec3(0.0,0.1,0.0);


   float light =  lineWidth*0.009 / abs( uv.y  -  lineWidth/2.0 ) ;


   float b = step(0.0 ,  sin(uv.x/5.10 + iTime ) );

   vec3 mix_color = mix(yellow * light , blue * light, b);

   float c =  bottom * top ;
   return mix(empty_color , mix_color  , c);


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord;

     vec3 color = vec3(0.0);



   float n = floor( iResolution.x  / 70.0 );

   for( float i = -n ; i <  2.0 * n ; i++ ){
      color+= line(  rotate(PI/3.0) * vec2 ( fragCoord.x - 90.0 * i  , fragCoord.y )  , 47.02  );

     color+= line( rotate(-PI/3.0) * vec2 ( fragCoord.x - 90.0 * i  , fragCoord.y )  , 47.02);

   }



   fragColor = vec4(color ,1.0);

}
