float hash12(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p =( fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
    vec2 scale = p*10.0;
    vec2 f = fract(scale);
    vec2 uv = f/0.01;
    vec2 cell = scale-f;
    float random = hash12(cell);
    float r = length(uv-vec2(49.*random));
    float i = sin(iTime);
    float c = 1.0-smoothstep(abs(cell.y+20.0)*random*i,abs(cell.y+20.0)*random+5.0,r);

    fragColor = vec4(c, c*1.0, 0.4,1.0);
}
