void mainImage( out vec4 rgba, in vec2 h2o )
{
    rgba=texture(iChannel0,h2o/iResolution.xy);
    vec2 R=iResolution.xy,M=iResolution.xy/2.;
    if(iMouse.x+iMouse.y>0.)M=iMouse.xy;
    h2o.x=(h2o.x-(M.x-R.x/2.))-R.x/2.;
    float r=length(h2o)/M.y*10.;
    if(r*20.<390.&&r*20.>250.){
        rgba+=vec4(sin(r-3.14),sin(r-1.57),sin(r),0)/3.0*clamp(sin(r-1.57),0.,1.);
    }
}
