// variant from oneshade's "2D Wave Lattice" https://shadertoy.com/view/ftySRt

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = 2.*(u+u - R) / R.y;

    float n = 13., TAU = 6.283, a=0.;

    for (O-=O; a < TAU; a += TAU / n)
        O += sin( dot(U, vec2(cos(a), sin(a))) * 40. + iTime + a
                  + fract(1e4*sin(1e4*a))*iTime ); // random phase

     O = .5 + O / sqrt(n)/4.;    // normalization
  // O = .5 + .5*O/fwidth(O);    // B & W variant
  // O = smoothstep(-1.,1.,O/fwidth(O)); // B & W variant

}
