const float SIZE = 40.0;
const vec4 BLACKNESS = vec4(170.0/255.0, 170.0/255.0, 170.0/255.0, 255.0/255.0);
const vec4 WHITENESS = vec4(221.0/255.0, 221.0/255.0, 221.0/255.0, 255.0/255.0);
const vec4 DIFF = WHITENESS - BLACKNESS;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{ 
    vec2 Pos = floor(fragCoord / SIZE);
    float PatternMask = mod(Pos.x + Pos.y, 2.0);
    fragColor = BLACKNESS + PatternMask * DIFF;
}
