void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 offset;
    offset.x = -distance(uv.x,0.5)/1000.0 - 0.0004;
    offset.y = distance(uv.y,0.5)/1000.0 + 0.0004;
    fragColor = vec4
    (
    	texture(iChannel0, uv - offset).r,
    	texture(iChannel0, uv).g,
    	texture(iChannel0, uv + offset).b,
    	1.0
    );
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,vec2(12.9898,78.233)))*43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col;
    float rad = distance(uv,vec2(0.))*70.;
    col += smoothstep(-1.8,2.,sin(rad-iTime) + random(uv)/4.);
    if (uv.x <= -.45 || uv.x >= .45 || uv.y <= -.45 || uv.y >= .45) {
        col = vec3(1.) - random(uv)/15.;
    }
    rad = distance(uv.y,.3);
    col -= pow(rad/1.2,3.);
    fragColor = vec4(col*vec3(1.,.99,.98),1.0)/1.1;
}
