void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);
    vec3 col_a = vec3(0.0);
    vec3 col_b = vec3(1.0, 0.0, 0.0);
    if(uv.x < 0.33)
    {
        col = col_a;
    }
    else if(uv.x >0.33 && uv.x < 0.66)
    {
        if(iFrame % 2 == 0)
        {
            col = col_a;
        }
        else
        {
            col = col_b;
        }
    }
    else
    {
        col = col_b;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
