vec3 bgColor = vec3(0.0, 0.46, 0.30);



vec3 sample_function(in float x, in float y) {


    // Disk: x*x + y*y = r*r
    float s = abs(x*x + y*y);
    if (s < 0.10) {
        return vec3(0.96, 0.165, 0.255);
    }
    else return bgColor;

}



/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner



   float width = iResolution.x;


   float height = iResolution.y; 



   float x = fragCoord.x;
   float y = fragCoord.y;

   // map (0.5, w-0.5) to (-1, 1)
   // and (0.5, h-0.5) to (-1, 1)
   x = (x/width)*2.0 - 1.0;
   y = (y/height)*2.0 - 1.0;

   float aspectRatio = height / width;
   y = aspectRatio * y;

   // sample the function
   vec3 samp = sample_function(x, y);

   fragColor = vec4(samp, 1.0);
}
