vec2 alterPosition (vec2 pos, vec2 mouse)
{
    // pinch
    if (false) {
        pos -= mouse;

        float len = 2.0*length(pos);
        float s = 2.0*(1.0-len)*(1.0-len)*(1.0-len) + 1.0;
        s = max(s,1.0);

        return pos*s + mouse;
    }

    // Pixellate
    if (true) {
        vec2 delta = (pos - mouse) * vec2(2.0,1.0);
        if (length(delta)<0.3)
        {
            pos = floor(pos*30.0)/30.0;
        }
        return pos;
    }
}

vec4 alterPixel (vec4 pixel, vec2 mouse)
{
    return pixel;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec2 pos = fragCoord.xy / iResolution.xy;

    pos = alterPosition(pos, mouse);
    vec4 c = texture(iChannel0,pos);

	fragColor = alterPixel(c, mouse);
}
