float circle(vec2 uv){
    return smoothstep(0.01,0.02,uv.y*uv.y+uv.x*uv.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    uv.x -= 0.9;
    uv.y -= 0.5;
    uv = fract(uv*10.);
    uv.x -= 0.5;
    uv.y -= 0.5;
    uv *= sin(iTime *0.1*uv.x*uv.y)*4.0;

    vec3 col = vec3(0.0,1.0,1.0);

    float y = circle(uv*sin(iTime*uv.x*uv.x*0.1)*cos(iTime*uv.y*0.1));

    col -= y;


    fragColor = vec4(col,1.0);
}
