#define LINESIZE 4.0
float nativeSmoothstep (float edge0, float edge1, float x)
{
   x = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
   return x * x * (3.0 - 2.0 * x);
}

float linearSmoothstep (float edge0, float edge1, float x)
{
   x = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
   return x;
}

float smootherstep(float edge0, float edge1, float x) {
  x = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
  return x * x * x * (x * (x * 6.0 - 15.0) + 10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float smh = nativeSmoothstep(0.0, iResolution.x, fragCoord.x) * iResolution.y;
    float sml = linearSmoothstep(0.0, iResolution.x, fragCoord.x) * iResolution.y;
    float sms = smootherstep(0.0, iResolution.x, fragCoord.x) * iResolution.y;
    smh = max(1.0 - abs(smh - fragCoord.y) / LINESIZE, 0.0);
    sml = max(1.0 - abs(sml - fragCoord.y) / LINESIZE, 0.0) * 0.5;
//    sms = max(1.0 - abs(sms - fragCoord.y) / LINESIZE, 0.0) * 0.5;
    fragColor = vec4(smh + sml); // + sms);
}
