const vec4 circleColor = vec4(1.0f,0.0f,0.0f,0.5f);
vec4 circleColorB = vec4(0.0f,0.0f,1.0f,0.5f);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //-1,-1 to 1,1 range
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;  
    vec4 col = vec4(1.0);
    float circleD = 1.0-smoothstep(0.25,0.35,length(uv));
    col = mix(col,circleColor,circleD * circleColor.a);

    circleD = 1.0-smoothstep(0.395,0.405,length(uv+vec2(0.3,0.3)));
    circleColorB.rgb += max(sin((uv.x + uv.y)*2.0 + iTime*3.0),0.0) * 0.2;
    col = mix(col,circleColorB,circleD * circleColorB.a);
    // Output to screen
    fragColor = col;
}
