void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += sin(fragCoord.y / 1000.0 + iTime * 10.0) / 2.0;
    uv.y += sin(fragCoord.x / 1000.0 + iTime) / 10.0;

    vec3 tex_col = texture(iChannel0, uv).xyz;

    fragColor = vec4(tex_col, 1.0);
}
