#define MAXX 10000000.0

float seed = 0.0;
#define HASHSCALE1 .1031
float hash12(vec2 p) {
    vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

struct Material {
vec3 baseColor;      
float metallic;      
float subsurface;    
float specular;      
float roughness;     
float specularTint;  
float anisotropic;   
float sheen;         
float sheenTint;     
float clearcoat;     
float clearcoatGloss;
};
// all the brdf code is from 
// https://github.com/wdas/brdf/blob/main/src/brdfs/disney.brdf
// Thanks to Brent Burley and disneyanimation.com
const float PI = 3.14159265358979323846;

float sqr(float x) { return x*x; }

float SchlickFresnel(float u)
{
    float m = clamp(1.0-u, 0.0, 1.0);
    float m2 = m*m;
    return m2*m2*m; // pow(m,5)
}

float GTR1(float NdotH, float a)
{
    if (a >= 1.0) return 1.0/PI;
    float a2 = a*a;
    float t = 1.0 + (a2-1.0)*NdotH*NdotH;
    return (a2-1.0) / (PI*log(a2)*t);
}

float GTR2(float NdotH, float a)
{
    float a2 = a*a;
    float t = 1.0 + (a2-1.0)*NdotH*NdotH;
    return a2 / (PI * t*t);
}

float GTR2_aniso(float NdotH, float HdotX, float HdotY, float ax, float ay)
{
    return 1.0 / (PI * ax*ay * sqr( sqr(HdotX/ax) + sqr(HdotY/ay) + NdotH*NdotH ));
}

float smithG_GGX(float NdotV, float alphaG)
{
    float a = alphaG*alphaG;
    float b = NdotV*NdotV;
    return 1.0 / (NdotV + sqrt(a + b - a*b));
}

float smithG_GGX_aniso(float NdotV, float VdotX, float VdotY, float ax, float ay)
{
    return 1.0 / (NdotV + sqrt( sqr(VdotX*ax) + sqr(VdotY*ay) + sqr(NdotV) ));
}

vec3 mon2lin(vec3 x)
{
    return vec3(pow(x[0], 2.2), pow(x[1], 2.2), pow(x[2], 2.2));
}


vec3 BRDF( vec3 L, vec3 V, vec3 N, vec3 X, vec3 Y, Material m)
{
    float NdotL = dot(N,L);
    float NdotV = dot(N,V);
    if (NdotL < 0.0 || NdotV < 0.0) return vec3(0.);

    vec3 H = normalize(L+V);
    float NdotH = dot(N,H);
    float LdotH = dot(L,H);

    vec3 Cdlin = mon2lin(m.baseColor);
    Cdlin = m.baseColor;
    float Cdlum = .3*Cdlin[0] + .6*Cdlin[1]  + .1*Cdlin[2]; // luminance approx.
    vec3 Ctint = Cdlum > 0.0 ? Cdlin/Cdlum : vec3(1); // normalize lum. to isolate hue+sat
    vec3 Cspec0 = mix(m.specular*.08*mix(vec3(1), Ctint, m.specularTint), Cdlin, m.metallic);
    vec3 Csheen = mix(vec3(1), Ctint, m.sheenTint);

    // Diffuse fresnel - go from 1 at normal incidence to .5 at grazing
    // and mix in diffuse retro-reflection based on roughness
    float FL = SchlickFresnel(NdotL), FV = SchlickFresnel(NdotV);
    float Fd90 = 0.5 + 2.0 * LdotH*LdotH * m.roughness;
    float Fd = mix(1.0, Fd90, FL) * mix(1.0, Fd90, FV);

    float fd_90_minus_1 = 2.0 * LdotH * LdotH * m.roughness - 0.5;

    Fd  = (1.0 + fd_90_minus_1 * pow(1.0 - NdotL, 5.0))
        * (1.0 + fd_90_minus_1 * pow(1.0 - NdotV, 5.0));


    // Based on Hanrahan-Krueger brdf approximation of isotropic bssrdf
    // 1.25 scale is used to (roughly) preserve albedo
    // Fss90 used to "flatten" retroreflection based on roughness
    float Fss90 = LdotH*LdotH*m.roughness;
    float Fss = mix(1.0, Fss90, FL) * mix(1.0, Fss90, FV);
    float ss = 1.25 * (Fss * (1.0 / (NdotL + NdotV) - .5) + .5);

    // specular
    float aspect = sqrt(1.0-m.anisotropic*.9);
    float ax = max(.001, sqr(m.roughness)/aspect);
    float ay = max(.001, sqr(m.roughness)*aspect);
    float Ds = GTR2_aniso(NdotH, dot(H, X), dot(H, Y), ax, ay);
    float FH = SchlickFresnel(LdotH);
    vec3 Fs = mix(Cspec0, vec3(1), FH);
    float Gs;
    Gs  = smithG_GGX_aniso(NdotL, dot(L, X), dot(L, Y), ax, ay);
    Gs *= smithG_GGX_aniso(NdotV, dot(V, X), dot(V, Y), ax, ay);
    Gs = 1.0; 
    // sheen
    vec3 Fsheen = FH * m.sheen * Csheen;

    // clearcoat (ior = 1.5 -> F0 = 0.04)
    float Dr = GTR1(NdotH, mix(.1,.001,m.clearcoatGloss));
    float Fr = mix(.04, 1.0, FH);
    float Gr = smithG_GGX(NdotL, .25) * smithG_GGX(NdotV, .25);
    //return 1.0/PI * Fd*Cdlin * (1.0-m.metallic);

    return ((1.0/PI) * mix(Fd, ss, m.subsurface)*Cdlin + Fsheen)
        * (1.0-m.metallic)
        + Gs*Fs*Ds + .25*m.clearcoat*Gr*Fr*Dr;
}


vec3 bgColor(vec3 rayDir) {
    float u =  0.5*(1.0 + rayDir[1]);
    return u*vec3(0.7, 0.8, 0.9) + (1.0-u)*vec3(0.05, 0.05, 0.2);
}

// random number between 0 and 1
float random() {
    return fract(sin(seed++)*43758.5453123);
}



// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
    float radius;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Intersection {
    int obj;
    float t;
};

const int NUM_SPHERE = 11;
Sphere spheres[NUM_SPHERE];
Material materials[NUM_SPHERE];
Light  lights[1];

// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t;
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return MAXX;
}

// Traverses the entire scene and 
// returns the objectID and the intersection point
Intersection intersectAllObjects(Ray ray) {
    float minT = MAXX;
    int iSphere = -1;

    for (int i=0; i < NUM_SPHERE; i++) {
       Sphere sphere = spheres[i];

       float t = raySphereIntersect(ray, sphere);

       if (t < minT && t >= 0.001) {
           // keep track of the closest sphere and intersection
           iSphere = i;
           minT = t;
       }
   }

   return Intersection(iSphere, minT);
}


// create 4 spheres at different locations in different colors
void makeScene(int f) {
    int count = 0;
    float radius = 0.2;
    float x = -0.5;
    float y = -0.3; 
    float z = -2.0;
    // place 9 spheres in a grid with some separation
    for (int i=0; i<3; i++) {
        for (int j=0; j<3; j++) {
            spheres[count] = Sphere(radius,vec3(x, y, z), vec3(0.0));
            materials[count].baseColor = vec3(random(), random(), random());
            x = x + (radius+0.3);
            count++;
        }
        x = -0.5;  // reset x for the next row
        z = z - (radius+0.5);
        y = y + 0.3;
    }
    spheres[9] = Sphere(0.0, vec3(0.0, -10.0, -70.0), vec3(1.0, 0.3, 0.3));
    spheres[10] = Sphere(0.2, vec3(1.0, 0.5, -2.20*float(f)/200.0), vec3(1, 1, 1)); 
    //spheres[0] = Sphere(0.2, vec3(0.0, 0, -1.0), vec3(1, 1, 0));
    lights[0] = Light(vec3(1.0, 0.5, -2.20*float(f)/200.0), vec3(10, 10, 10), 0.2);
    materials[0].baseColor = vec3(0.81, 0.42, 0.0);// 0.815, .418501512, .00180012
    materials[0].metallic = 0.2; // .0; 
    materials[0].subsurface = 0.0;
    materials[0].specular = 0.0;
    materials[0].roughness = 0.2;
    materials[0].specularTint = 0.0;
    materials[0].anisotropic = 0.0;
    materials[0].sheen = 0.0;
    materials[0].sheenTint = 0.0;
    materials[0].clearcoat = 0.0;
    materials[0].clearcoatGloss = 0.0;
    materials[9].baseColor = vec3(0.25, 0.25, 0.25);

}

// samples per pixel
const int numSamples=16;

// convert directionToLight and directionToView to tangent space
//
void convertToTangentSpace(vec3 toLight, vec3 toView, vec3 hitPoint, out vec3 toLightTS, out vec3 toViewTS, out vec3 nTS)
{
    // use a matrix to convert
    vec3 t = normalize(dFdx(hitPoint));
    vec3 b = normalize(dFdy(hitPoint));
    vec3 n = normalize(cross(t, b));
    mat3 xformMatrix = transpose(mat3(t, b, n));

    toLightTS = xformMatrix * toLight;
    toViewTS = xformMatrix * toView;
    nTS = n;
}

float checkLightVisibility(in Light light, vec3 wi, vec3 hitPoint, vec3 hitNormal) {
    float visible = 0.0;
    vec3 lightDir = wi; 
    Ray ray;
    ray.origin = hitPoint+lightDir*0.01;
    ray.direction = lightDir;
    Intersection intersection = intersectAllObjects(ray);
    int iSphere = intersection.obj;
    if (iSphere == 10) { 
        // light was hit, light is visible
        visible = 1.0;
    }
    return visible;
}

void
makeOrthonormalBasis(vec3 normal, out vec3 tangent, out vec3 binormal) {
    if (abs(normal.x) > abs(normal.y)) {
        tangent = normalize(vec3(0., normal.z, -normal.y));
    }
    else {
        tangent = normalize(vec3(-normal.z, 0., normal.x));
    }

    binormal = cross(normal, tangent);
}



vec3 sampleCone(vec2 u12, float cosThetaMax, vec3 xbasis, vec3 ybasis, vec3 zbasis) {
    float cosTheta = (1. - u12.x) + u12.x * cosThetaMax;
    float sinTheta = sqrt(1. - cosTheta * cosTheta);
    float phi = u12.y * 2.0*PI;
    vec3 sphericald = vec3(sinTheta * cos(phi), sinTheta * sin(phi), cosTheta);
    return sphericald.x * xbasis + sphericald.y * ybasis + sphericald.z * zbasis;
}


vec3 sampleLight(Light light, vec3 hitPoint, vec3 hitNormal, out vec3 wi, out float lightPdf) {
    vec2 u = vec2(random(), random());

    vec3 tangent = vec3(0.);
    vec3 binormal = vec3(0.);
    vec3 lightDir = normalize(light.location - hitPoint);
    makeOrthonormalBasis(lightDir, tangent, binormal);

    float d = length(light.location-hitPoint);
    float sinThetaMax2 = light.radius * light.radius / (d*d);
    float cosThetaMax = sqrt(max(0.00001, 1. - sinThetaMax2));
    wi = sampleCone(u, cosThetaMax, tangent, binormal, lightDir);

    if (dot(wi, hitNormal) > 0.) {
        lightPdf = 1. / (2.0*PI * (1. - cosThetaMax));
    }

	return light.color;
}


/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner

   makeScene(iFrame);
   //materials[0].roughness = iMouse.x/iResolution.x;
   //materials[0].metallic = iMouse.y/iResolution.y;
   vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<1*numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       seed = float(iFrame) +  hash12(vec2(x,y));
       // normalized ray direction
       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);

       vec3 L = vec3(0.0, 0.0, 0.0);
       for (int bounces=0; bounces<2; bounces++) {

       // traverse the scene (all spheres) and find the 
       // closest intersected object and intersection point
       Intersection intersection = intersectAllObjects(ray);

       int iSphere = intersection.obj;
       float minT = intersection.t;

       Sphere sphere;

       if (iSphere > -1) { // if there is an intersection

           // to get around iSphere not being constant
           for (int i=0; i<NUM_SPHERE; i++) {
               if (i==iSphere) {
                   sphere = spheres[i];
                   break;
               }
           }

           if (iSphere == 10 && bounces==0) {
               // direct hit to the light from view
               L = lights[0].color; // 'show' the light
               break;
           }
           if (iSphere==10) break; // otherwise ignore it


           Material m = materials[iSphere];
           vec3 wi;
           float lightPdf = 0.0;
           vec3 Ld = vec3(0.0, 0.0, 0.0);

           // hit coordinates
           vec3 hit = ray.origin + minT*ray.direction;
           // normal at the point of ray-sphere intersection
           vec3 hitPointNormal = normalize(hit-sphere.center);         
           // vector from intersection to light
           vec3 hitPointToLight = normalize(lights[0].location-hit);
           vec3 hitPointToView = ray.origin-hit;
           // calculate tangent and binormal at hit point
           vec3 tangent = cross(hitPointNormal, vec3(1.,0.,1.));
           vec3 binormal = normalize(cross(hitPointNormal, tangent));
           tangent = normalize(cross(hitPointNormal,binormal));


           // sample the light (get new direction and pdf)
           vec3 Li = sampleLight(lights[0], hit, hitPointNormal, wi, lightPdf);
           //wi = hitPointToLight;
           float lightVisible = checkLightVisibility(lights[0], wi, hit, hitPointNormal);
           Li = Li * lightVisible;

           // compute the brdf at intersection
           vec3 f = BRDF(wi, hitPointToView, hitPointNormal, tangent, binormal, m);
           f = f * abs(dot(wi, hitPointNormal));

           if (lightVisible > 0. && lightPdf > 0.0001)
               Ld = Li * f  / lightPdf;  // direct light contribution


           L = L + Ld; // accumulate direct light
           // the new ray
           ray.direction = wi;
           ray.origin = hit + .01 * ray.direction;


       } 
       else {
           L = bgColor(ray.direction); // nothing was hit

       }

       } // end of bounces
       samp = samp + L;
   } // end of spp
   // average all the samples per pixel
   samp = samp/float(numSamples);
   // tone map
   vec3 toneMappedColor = samp * (1.0 / (samp + 1.0));
   // gamma correct
   float gamma = 1.0/2.2;
   vec3 finalColor = vec3(pow(toneMappedColor.x, gamma), 
                          pow(toneMappedColor.y, gamma), 
                          pow(toneMappedColor.z, gamma));

   fragColor = vec4(finalColor, 1.0);

}
