vec3 draw(vec2 uv) {
    return vec3(texture(iChannel0,vec2(uv.x,1.-uv.y)));
}

float terrain(float x) {

    float v = 0.; 
    x *= 3.;
    #define l 13.
    for (float n = 0.; n < l; n++) {
        v += ((cos((x*sin(n/240.))+(n/1.72)))/l)*3.;
    }
    return pow(v, -0.12);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime / 10.;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 dist_texture = vec3(draw(uv+(terrain((uv.y*14.)+(time*30.))/202.)));
    fragColor = vec4(dist_texture, 1.0);
}
