vec3 coolTone( float b )
{
    return vec3(max(3.0*(b-0.3)*(b-0.3),1.2*b-0.1), max(0.0, 1.7*b-0.6), max(-b/2.0+0.3, 2.0*b*b-0.8));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    vec3 col2 = coolTone((col.r+col.g+col.b)/3.0);

    float weight = 3.0;

    fragColor = vec4((col+col2*weight)/(weight+1.0),1.0);
}
