void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 0.5f;

    float NoiseVal = fract(sin(dot(fragCoord * 2.0f, vec2(12.2929, 78.293))) + 39389.0f);

    //vec3 col = vec3(NoiseVal);
    // Output to screen
    float G = 0.1f/clamp( sin( iTime * uv.x ), 0.001f, 1.0f );

    vec3 col = vec3(fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * sin(iTime  + uv.y),
        fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * G,
            fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * sin(sin(iTime) + .5f * NoiseVal * uv.x));
    //col = vec3(  );
    fragColor = vec4(col,1.0);
}
