void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 localPixelCoord = fragCoord.xy / iResolution.xy;
    vec2 size = vec2(9.0) / iResolution.xy;

    // bottom-left
    vec2 bl = step(size, localPixelCoord);
    float interpolation = bl.x * bl.y;

    // top-right
    vec2 tr = step(size, 1.0 - localPixelCoord);
    interpolation *= tr.x * tr.y;

    interpolation = 1.0 - interpolation;

    float stop = 0.25;
    float borderAlpha = 1.0 - (1.0 - localPixelCoord.y) * (1.0 / stop);
    borderAlpha = borderAlpha * 1.0 - (localPixelCoord.x) * (1.0 / stop);

    // borderAlpha = 1.0 - step(stop, localPixelCoord.x);
    // borderAlpha *= 1.0 - step(stop, 1.0 - localPixelCoord.y);

    vec4 borderColor = vec4(vec3(227.0, 103.0, .0) / 255.0, 1.0);
    vec4 backgroundColor = vec4(vec3(15.0, 26.0, 46.0) / 255.0, 1.0);

    fragColor = mix(backgroundColor, borderColor, max(borderAlpha, 0.0) * interpolation);
}
