float PI = 3.14,
      R = .8,
      n = 14.,
      m = 9.,
      z = 10.,
      A = 0.2,
      F = 3.14,
      P = 2.,
      FA = 1.,
      V = 1.;
vec2 c = vec2(-.8, .4);
vec4 w = vec4(.0, 0., 1., 1.);

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = z * ( 2. * u - Res ) / Res.y;

    O = vec4(0.);
    float C = .2;
    float D = 0.03;
    float r = distance(U, z * c);
    vec3 star = vec3(min(exp((-r + R)/.05), 1.) * abs(1./C));

    for(float i = 1.; i < m; i++) {
        float Rd = R * C * i;
        float R = R * i;
        for(float j = 0.; j < n; j++) {
            float a = R * (j+1.) * (2. * PI)/n + iTime/10.;
            float b = i * (2. * PI)/m;
            r = distance(U - z * c, Rd * vec2(-sin(a) + 1.7, cos(a) - 1.7));
            O += vec4(min(exp((-r + R)/.02), 0.8 * sin(iTime/2.) + 1.5)) * vec4(abs(sin(iTime + a))) * C * D;

            float y = A * sin(F * U.x + P * sin(iTime) + a) * cos(FA * iTime * a/100.);
            O += w * min(exp((U.y - y + z * (V * 3./4. - V/4. * sin(a)))/0.05), 1.) * C * D;
        }
    }

    O += vec4(star, 1.);
}
