void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float ratio = iResolution.x / iResolution.y;
    float time = iTime/10.0;
	vec2 uv = vec2(ratio, 1.) * (2. * fragCoord.xy / iResolution.xy - 1.);

    //rotate the uv vector
    float oldx = uv.x;
    vec2 rotUV;
    rotUV.x = uv.x * cos(iTime) - uv.y * sin(iTime);
    rotUV.y = oldx * sin(iTime) + uv.y * cos(iTime);

    //Make the color gradiant on the sphere with the rotated uv
    vec3 n = vec3(rotUV.x, rotUV.y, sqrt(1. - clamp(dot(uv, uv), 0., 1.)));
    vec3 color = .5 + .5*n;

    if(uv.x < 0. && uv.x > -2.) 
        if( uv.y < 1.75 && uv.y >-1.75)
            color = clamp(sin(iTime), 0.5, 1.) + clamp(cos(iTime), 0., 1.)*n;    
    if(uv.x < 2. && uv.x > 0.)
        if( uv.y < 1.75 && uv.y >-1.75)
             color = clamp(cos(iTime), 0.5, 1.) + clamp(sin(iTime), 0., 1.)*n; 

    float gradientValue = color.g; 

    //apply the gradient to channel 0
    vec4 tex = texture(iChannel0, uv);
    vec3 color2 = vec3(0);

