            color2 = mix(vec3(0.5), vec3(gradientValue)+tex.rgb, smoothstep(1.01, 1., dot(uv, uv)));
            color2 *= clamp(0.5+cos((time+vec3(0.7,0.8,1.0))*5.0), 0.25, 1.0);
        }

