void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - (iResolution.xy / vec2(2.0));
    float x = float(coord.x);
    float y = float(coord.y);
    float r = (sqrt(x*x+y*y));
    fragColor=vec4(0.2,0.8,0.2,1);//green
    if((x+83.0)*sqrt(3.0)/2.0+y*0.5>0.0&&y>-30.0)fragColor=vec4(1,0.1,0.1,0);//red
    if(r>=72.0&&x>0.0&&y<72.0)fragColor=vec4(0.2,0.8,0.2,1);//green
    if((y)*0.5-(x-83.0)*sqrt(3.0)/2.0<0.0&&y<72.0)fragColor=vec4(1,0.9,0,0);//yellow
    if(r>=72.0&&x>0.0&&y<72.0&&y>-30.0)fragColor=vec4(1,0.9,0,0);//yellow
    if(r<72.0)fragColor=vec4(1);//white
    if(r<64.0)fragColor=vec4(0,0.3,1,0);//blue
    if(r>160.0)fragColor=vec4(0);//black
    //shadows
    if(r>=72.0&&r<84.0)fragColor-=abs(r-84.0)/140.0;
    if(r<=160.0&&r>148.0)fragColor-=abs(r-148.0)/140.0;
    if(fragColor.z<1.0&&(x+83.0)*sqrt(3.0)/2.0+y*0.5>-2.0&&(x+83.0)*sqrt(3.0)/2.0+y*0.5<2.0&&y>-30.0)
        fragColor-=vec4(0.1);
    if((y)*0.5-(x-83.0)*sqrt(3.0)/2.0<2.0&&(y)*0.5-(x-83.0)*sqrt(3.0)/2.0>-2.0&&fragColor.z<1.0&&y<-30.0)
        fragColor-=vec4(0.1); 
    if(y>=70.0&&y<=74.0&&x>0.0&&fragColor.z<1.0)fragColor-=vec4(0.1);
}
