#define freq(f) texture(iChannel0, vec2(f, 0.25)).x * 0.8
#define wave(f) texture(iChannel0, vec2(f, 0.75)).x

// Fast enough to go fullscreen on almost anything!
// WIP just make it better :D 
// @Gimulnautti

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv1b = uv - vec2( 0.5 + sin(iTime) * 0.05, 0.5 + cos(iTime) * 0.1 );    
    vec2 uv1 = uv1b / dot( uv1b, uv1b * (freq( 100 )) );
    float line1 = cos( uv1.x + freq(50) * 10.0 + iTime );

    vec2 uv2b = uv - vec2( 0.5 - sin(iTime) * 0.05, 0.5 + cos(iTime) * 0.1 );    
    vec2 uv2 = uv2b / dot( uv2b, uv2b * (freq( 1000 ) + 0.1 + sin(iTime/2.0)) );
    float line2 = cos( uv2.y + freq(2000) * 1.0 - iTime * 20.0 );

    vec2 uv3b = uv - vec2( 0.5 /*0.3 + sin(freq(150))* 0.5*/, 0.5 - cos(iTime) * 0.1 );    
    vec2 uv3 = uv3b / (dot( uv3b, uv3b * (freq( 4000 )) ) * 0.5);
    float line3 = cos( uv3.y + uv3.x + freq(10000) * 2.0 - iTime * 10.0 );

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos(iTime+freq(50)*4.0+uv.xyx+vec3(0,2,4));
    vec3 col2 = vec3( line2/line1, 0.0, line1/line3 );

    // Output to screen
    fragColor = vec4(col1 + col2,1.0);
}
